package operator;

public class ShiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=2 , b=32 ,c;
		
		c= a >> 2;                  // Right shift
		System.out.println("Right Shift : "+c);
		
		
		c= b << 3;
		System.out.println("Left Shift : "+c);

	}

}
